use elements::locktime::LockTime;
use elements::taproot::ControlBlock;
use elements::{BlockHash, Transaction};
use simplicity::hashes::Hash;
use simplicity::jet::elements::{ElementsEnv, ElementsUtxo};
use simplicity::Cmr;
use simplicity::{bitcoin, elements};
use std::sync::Arc;

pub struct EnvSampling {
    /// Number of inputs in the transaction
    n_in: usize,
    /// Number of outputs in the transaction
    n_out: usize,
    /// The annex to put into the first input of the transaction, if any.
    annex: Option<Vec<u8>>,
    /// Type of the environment
    ty: EnvSamplingType,
}

enum EnvSamplingType {
    /// Null Elements environment (it is not being used)
    Null,
    /// An Elements environment with issuance. The zeroth input is the issuance
    /// input, other inputs are combination of explicit and confidential inputs.
    Issuance,
    /// An Elements environment with pegin. The zeroth input is the pegin input,
    /// other inputs are combination of explicit and confidential inputs.
    Pegin,
    /// An Elements environment with a confidential transaction spend
    /// The zeroth input is the confidential transaction spend input, other
    /// inputs are combination of explicit and confidential inputs.
    ConfidentialTxSpend,
    /// An Elements environment with explicit tx spend.
    /// The zeroth input is the explicit transaction spend input, other
    /// inputs are combination of explicit and confidential inputs.
    ExplicitTxSpend,
}

impl EnvSampling {
    /// Private constructor
    fn from_sampling_ty(ty: EnvSamplingType) -> Self {
        EnvSampling {
            n_in: 0,
            n_out: 0,
            annex: None,
            ty,
        }
    }

    /// Null Elements environment (it is not being used)
    pub fn null() -> Self {
        Self::from_sampling_ty(EnvSamplingType::Null)
    }

    /// An Elements environment with issuance. The zeroth input is the issuance
    /// input, other inputs are combination of explicit and confidential inputs.
    pub fn issuance() -> Self {
        Self::from_sampling_ty(EnvSamplingType::Issuance)
    }

    /// An Elements environment with pegin. The zeroth input is the pegin input,
    /// other inputs are combination of explicit and confidential inputs.
    pub fn pegin() -> Self {
        Self::from_sampling_ty(EnvSamplingType::Pegin)
    }

    /// An Elements environment with a confidential transaction spend
    /// The zeroth input is the confidential transaction spend input, other
    /// inputs are combination of explicit and confidential inputs.
    pub fn confidential_tx_spend() -> Self {
        Self::from_sampling_ty(EnvSamplingType::ConfidentialTxSpend)
    }

    /// An Elements environment with explicit tx spend.
    /// The zeroth input is the explicit transaction spend input, other
    /// inputs are combination of explicit and confidential inputs.
    pub fn explicit_tx_spend() -> Self {
        Self::from_sampling_ty(EnvSamplingType::ExplicitTxSpend)
    }

    /// A randomized Elements environment with no inputs or outputs (call
    /// [`Self::n_inputs`] and [`Self::n_outputs`] to add inputs and outputs.
    pub fn random(seed: usize) -> Self {
        let mut base = match seed % 4 {
            0 => Self::confidential_tx_spend(),
            1 => Self::explicit_tx_spend(),
            2 => Self::pegin(),
            3 => Self::issuance(),
            _ => unreachable!(),
        };
        if seed & 8 == 0 {
            // The actual annex gets hashed, so its length and contents don't matter.
            base = base.annex_data(&[0xde, 0xad, 0xbe, 0xef]);
        }
        base
    }

    /// Attach a number of inputs to the environment
    pub fn n_inputs(self, n_in: usize) -> Self {
        EnvSampling { n_in, ..self }
    }

    /// Attach a number of inputs to the environment
    pub fn n_outputs(self, n_out: usize) -> Self {
        EnvSampling { n_out, ..self }
    }

    /// Attach an annex to the environment
    pub fn annex_data(self, data: &[u8]) -> Self {
        let mut annex = Vec::with_capacity(data.len() + 1);
        // same annex prefix in bitcoin and elements
        annex.push(bitcoin::taproot::TAPROOT_ANNEX_PREFIX);
        annex.extend(data.iter().copied());
        EnvSampling {
            annex: Some(annex),
            ..self
        }
    }

    /// Obtain a random environment from the sampler.
    pub fn env(&self) -> ElementsEnv<Arc<Transaction>> {
        let (txin, spent_utxo) = match self.ty {
            EnvSamplingType::Null => return null_env(),
            EnvSamplingType::Issuance => txin_utils::issuance(),
            EnvSamplingType::Pegin => txin_utils::pegin(),
            EnvSamplingType::ConfidentialTxSpend => txin_utils::confidential(),
            EnvSamplingType::ExplicitTxSpend => txin_utils::explicit(),
        };
        // Add the first input
        let mut tx_ins = vec![txin];
        let mut utxos = vec![spent_utxo];
        // Add the remaining inputs
        for _ in 0..self.n_in.checked_sub(1).expect("Atleast one input") {
            let (txin, spent_utxo) = if rand::random() {
                txin_utils::confidential()
            } else {
                txin_utils::explicit()
            };
            tx_ins.push(txin);
            utxos.push(spent_utxo);
        }
        // Add the outputs
        let mut tx_outs = Vec::with_capacity(self.n_out);
        for _ in 0..self.n_out {
            let txout = if rand::random() {
                txout_utils::confidential()
            } else {
                txout_utils::explicit()
            };
            tx_outs.push(txout);
        }
        let tx = Transaction {
            version: u32::default(),
            lock_time: LockTime::ZERO,
            input: tx_ins,
            output: tx_outs,
        };
        env_with_spent_utxos(tx, utxos, self.annex.clone())
    }
}

fn env_with_spent_utxos(
    tx: Transaction,
    utxos: Vec<ElementsUtxo>,
    annex: Option<Vec<u8>>,
) -> ElementsEnv<Arc<Transaction>> {
    let ctrl_blk: [u8; 33] = [
        0xc0, 0xeb, 0x04, 0xb6, 0x8e, 0x9a, 0x26, 0xd1, 0x16, 0x04, 0x6c, 0x76, 0xe8, 0xff, 0x47,
        0x33, 0x2f, 0xb7, 0x1d, 0xda, 0x90, 0xff, 0x4b, 0xef, 0x53, 0x70, 0xf2, 0x52, 0x26, 0xd3,
        0xbc, 0x09, 0xfc,
    ];
    ElementsEnv::new(
        Arc::new(tx),
        utxos,
        u32::default(),
        Cmr::from_byte_array([0xab; 32]), // dummy values
        ControlBlock::from_slice(&ctrl_blk).unwrap(),
        annex,
        BlockHash::all_zeros(), // Dummy genesis hash
    )
}

fn null_env() -> ElementsEnv<Arc<Transaction>> {
    let tx = Transaction {
        version: u32::default(),
        lock_time: LockTime::ZERO,
        input: Vec::default(),
        output: Vec::default(),
    };
    env_with_spent_utxos(tx, Vec::default(), None)
}

pub(super) mod txout_utils {
    use bitcoin::key::XOnlyPublicKey;
    use elements::hex::FromHex;
    use elements::{confidential, encode::deserialize, schnorr::TapTweak, AssetId, Transaction};
    use simplicity::{bitcoin, elements};

    const CONF_TX_BYTES : &str = "";
    /// Creates a TxOut with explicit asset and amount
    pub(super) fn explicit() -> elements::TxOut {
        // random 64 bit value
        // Appx random as we take mode over 21 mil, but really does
        // not matter
        let v = rand::random::<u64>() % 21_000_000;
        let asset = rand::random::<[u8; 32]>();
        let xpk = "21ddd128bfa066994cb4be6880f0589075996203d8a7e88f136eec662b8912b8";
        let xpk = Vec::<u8>::from_hex(xpk).unwrap();
        let output_key = XOnlyPublicKey::from_slice(&xpk)
            .unwrap()
            .dangerous_assume_tweaked();
        elements::TxOut {
            value: confidential::Value::Explicit(v),
            asset: confidential::Asset::Explicit(AssetId::from_slice(&asset).unwrap()),
            nonce: confidential::Nonce::Null,
            script_pubkey: elements::Script::new_v1_p2tr_tweaked(output_key),
            witness: elements::TxOutWitness::default(),
        }
    }

    pub(super) fn confidential() -> elements::TxOut {
        let tx_bytes = Vec::<u8>::from_hex(CONF_TX_BYTES).unwrap();
        let mut tx: Transaction = deserialize(&tx_bytes).unwrap();
        tx.output.swap_remove(0)
    }
}
pub(super) mod txin_utils {
    use elements::{
        confidential, encode::deserialize, hex::FromHex, AssetIssuance, OutPoint, Script, Sequence,
        TxInWitness, Txid,
    };
    use simplicity::{elements, jet::elements::ElementsUtxo};

    use super::txout_utils;
    use super::Hash;

    const PEGIN_TX_BYTES: &str = "";
    const ISSUANCE_TX_BYTES: &str ="";

    /// Creates a TxIn spending a txout explicit asset and amount
    /// Returns the txin and the spending utxo
    pub(super) fn explicit() -> (elements::TxIn, ElementsUtxo) {
        let txout = txout_utils::explicit();
        let tx_bytes = rand::random::<[u8; 32]>();
        let vout = rand::random::<u16>() as u32;
        let txin = elements::TxIn {
            previous_output: OutPoint::new(Txid::from_byte_array(tx_bytes), vout),
            is_pegin: false,
            script_sig: Script::new(),
            sequence: Sequence::MAX,
            asset_issuance: AssetIssuance::default(),
            witness: TxInWitness::default(),
        };
        (txin, txout.into())
    }

    /// Creates a TxIn spending a confidential txout
    /// Returns the txin and the spending utxo
    pub(super) fn confidential() -> (elements::TxIn, ElementsUtxo) {
        let txout = txout_utils::confidential();
        let tx_bytes = rand::random::<[u8; 32]>();
        let vout = rand::random::<u16>() as u32;
        let txin = elements::TxIn {
            previous_output: OutPoint::new(Txid::from_byte_array(tx_bytes), vout),
            is_pegin: false,
            script_sig: Script::new(),
            sequence: Sequence::MAX,
            asset_issuance: AssetIssuance::default(),
            witness: TxInWitness::default(),
        };
        (txin, txout.into())
    }

    /// Creates a TxIn spending a peg-in txout.
    /// The peg-in bitcoin txout is converted into elements one assuming the
    /// native bitcoin asset.
    /// Returns the txin and the spending utxo
    pub(super) fn pegin() -> (elements::TxIn, ElementsUtxo) {
        let tx_bytes = Vec::<u8>::from_hex(PEGIN_TX_BYTES).unwrap();
        let mut tx: elements::Transaction = deserialize(&tx_bytes).unwrap();
        let txin = tx.input.swap_remove(0);
        assert!(txin.is_pegin);
        let pegin_data = txin.pegin_data().expect("Is pegin");
        let bitcoin_utxo = ElementsUtxo {
            asset: confidential::Asset::Explicit(pegin_data.asset),
            value: confidential::Value::Explicit(pegin_data.value),
            script_pubkey: Script::new(), // Actually generated from a tweak of claim script and signing scripts
        };
        (txin, bitcoin_utxo)
    }

    /// Input with issuance as well as re-issuance
    pub(super) fn issuance() -> (elements::TxIn, ElementsUtxo) {
        let tx_bytes = Vec::<u8>::from_hex(ISSUANCE_TX_BYTES).unwrap();
        let mut tx: elements::Transaction = deserialize(&tx_bytes).unwrap();
        let txin = tx.input.swap_remove(0);
        assert!(txin.has_issuance());
        let txout = txout_utils::confidential();
        (txin, txout.into())
    }
}
